mapper.select:
	SELECT 
      ID_ITEM , 
      DESCRIPCION , 
      CANTIDAD , 
      PRECIO_UNITARIO , 
      ID_BOLETA 
	FROM
	ITEM
	WHERE 1 = 1

		AND ID_ITEM = #{id, jdbcType=INTEGER}


		AND DESCRIPCION = #{descripcion}


		AND CANTIDAD = #{cantidad, jdbcType=INTEGER}


		AND PRECIO_UNITARIO = #{precioUnitario, jdbcType=NUMERIC}


		AND ID_BOLETA = #{idBole.id, jdbcType=INTEGER}

mapper.insert:
INSERT INTO ITEM( ID_ITEM , DESCRIPCION , CANTIDAD , PRECIO_UNITARIO , ID_BOLETA ) (
SELECT #{element.id} , #{element.descripcion} , #{element.cantidad} , #{element.precioUnitario} , #{element.idBole.id} from dual
)

mapper.delete:
    DELETE FROM ITEM
	WHERE 1 = 1

		AND ID_ITEM = #{id, jdbcType=INTEGER}


		AND DESCRIPCION = #{descripcion}


		AND CANTIDAD = #{cantidad, jdbcType=INTEGER}


		AND PRECIO_UNITARIO = #{precioUnitario, jdbcType=NUMERIC}


		AND ID_BOLETA = #{idBole.id}
