mapper.select:
	SELECT 
      ID_LOTE , 
      STOCK , 
      ID_PRODUCTO , 
      PRECIO 
	FROM
	LOTE
	WHERE 1 = 1

		AND ID_LOTE = #{id, jdbcType=INTEGER}


		AND STOCK = #{stock, jdbcType=INTEGER}


		AND ID_PRODUCTO = #{idProduc.id, jdbcType=INTEGER}


		AND PRECIO = #{precio, jdbcType=NUMERIC}

mapper.insert:
INSERT INTO LOTE( ID_LOTE , STOCK , ID_PRODUCTO , PRECIO ) (
SELECT #{element.id} , #{element.stock} , #{element.idProduc.id} , #{element.precio} from dual
)

mapper.delete:
    DELETE FROM LOTE
	WHERE 1 = 1

		AND ID_LOTE = #{id, jdbcType=INTEGER}


		AND STOCK = #{stock, jdbcType=INTEGER}


		AND ID_PRODUCTO = #{idProduc.id}


		AND PRECIO = #{precio, jdbcType=NUMERIC}
