mapper.select:
	SELECT 
      ID_PRODUCTO , 
      DESCRIPCION , 
      PRESENTACION 
	FROM
	PRODUCTO
	WHERE 1 = 1

		AND ID_PRODUCTO = #{id, jdbcType=INTEGER}


		AND DESCRIPCION = #{descripcion}


		AND PRESENTACION = #{presentacion}

mapper.insert:
INSERT INTO PRODUCTO( ID_PRODUCTO , DESCRIPCION , PRESENTACION ) (
SELECT #{element.id} , #{element.descripcion} , #{element.presentacion} from dual
)

mapper.delete:
    DELETE FROM PRODUCTO
	WHERE 1 = 1

		AND ID_PRODUCTO = #{id, jdbcType=INTEGER}


		AND DESCRIPCION = #{descripcion}


		AND PRESENTACION = #{presentacion}
