mapper.select:
	SELECT 
      ID_VENTA , 
      FECHA_VENTA , 
      ID_LOTE , 
      ID_BOLETA 
	FROM
	VENTA
	WHERE 1 = 1

		AND ID_VENTA = #{id, jdbcType=INTEGER}


		AND FECHA_VENTA = #{fechaVenta, jdbcType=DATE}


		AND ID_LOTE = #{idLo.id, jdbcType=INTEGER}


		AND ID_BOLETA = #{idBole.id, jdbcType=INTEGER}

mapper.insert:
INSERT INTO VENTA( ID_VENTA , FECHA_VENTA , ID_LOTE , ID_BOLETA ) (
SELECT #{element.id} , #{element.fechaVenta} , #{element.idLo.id} , #{element.idBole.id} from dual
)

mapper.delete:
    DELETE FROM VENTA
	WHERE 1 = 1

		AND ID_VENTA = #{id, jdbcType=INTEGER}


		AND FECHA_VENTA = #{fechaVenta, jdbcType=DATE}


		AND ID_LOTE = #{idLo.id}


		AND ID_BOLETA = #{idBole.id}
